const Student = require('../Models/Student');
const bcrypt = require('bcrypt');
const { generateToken } = require('../utlis/jwtHelpers');

// STUDENT REGISTRATION
exports.register = async (req, res) => {
    const {
        studentID,
        firstName,
        lastName,
        fathersName,
        mothersName,
        Address,
        grade,
        email,
        password
    } = req.body;

    try {
        // Check if all required fields are provided
        if (!studentID || !firstName || !fathersName || !mothersName || !Address || !grade || !email || !password) {
            return res.status(400).json({
                message: "All required fields must be provided",
                required: ["studentID", "firstName", "fathersName", "mothersName", "Address", "grade", "email", "password"]
            });
        }

        // Check if student already exists
        const existingStudent = await Student.findOne({
            $or: [
                { studentID: studentID },
                { email: email }
            ]
        });

        if (existingStudent) {
            if (existingStudent.studentID === studentID) {
                return res.status(409).json({ message: "Student ID already exists" });
            }
            if (existingStudent.email === email) {
                return res.status(409).json({ message: "Email already registered" });
            }
        }

        // Hash the password before saving
        const saltRounds = 10;
        const hashedPassword = await bcrypt.hash(password, saltRounds);

        // Create new student with hashed password
        const newStudent = new Student({
            studentID,
            firstName,
            lastName: lastName || '',
            fathersName,
            mothersName,
            Address,
            grade,
            email,
            password: hashedPassword // Save the hashed password
        });

        // Save the student to database
        await newStudent.save();

        // Generate JWT token for immediate login after registration
        const token = generateToken({
            studentId: newStudent._id,
            studentID: newStudent.studentID
        });

        res.status(201).json({
            message: "Registration successful",
            studentID: newStudent.studentID,
            token: token,
            expiresIn: '24h'
        });

    } catch (err) {
        console.error("Registration Error:", err);

        // Handle specific MongoDB errors
        if (err.code === 11000) {
            const field = Object.keys(err.keyPattern)[0];
            return res.status(409).json({
                message: `${field} already exists`
            });
        }

        res.status(500).json({ message: "Server error during registration" });
    }
};

