backend/scripts/hashExistingPasswords.js.content:
/**
 * Utility script to hash existing plain text passwords in the database
 * Run this script once to convert all plain text passwords to hashed passwords
 * Usage: node scripts/hashExistingPasswords.js
 */

require('dotenv').config({ path: '../.env' });
const mongoose = require('mongoose');
const bcrypt = require('bcrypt');
const Student = require('../Models/Student');

// Connect to MongoDB
const connectDB = async () => {
    try {
        await mongoose.connect(process.env.MONGODB_URI || 'mongodb://localhost:27017/studentDB', {
            useNewUrlParser: true,
            useUnifiedTopology: true
        });
        console.log('âœ… Connected to MongoDB');
    } catch (error) {
        console.error('âŒ MongoDB connection error:', error);
        process.exit(1);
    }
};

// Function to check if a password is already hashed
const isPasswordHashed = (password) => {
    // bcrypt hashes always start with $2a$, $2b$, or $2y$
    return password && (password.startsWith('$2a$') || password.startsWith('$2b$') || password.startsWith('$2y$'));
};

// Main function to hash passwords
const hashExistingPasswords = async () => {
    try {
        console.log('ðŸ”„ Starting password hashing process...');

        // Find all students
        const students = await Student.find({});
        console.log(`ðŸ“Š Found ${students.length} students in database`);

        let hashedCount = 0;
        let alreadyHashedCount = 0;
        let errorCount = 0;

        for (const student of students) {
            try {
                // Check if password is already hashed
                if (isPasswordHashed(student.password)) {
                    alreadyHashedCount++;
                    console.log(`â­ï¸  Student ${student.studentID}: Password already hashed`);
                    continue;
                }

                // Hash the plain text password
                const saltRounds = 10;
                const hashedPassword = await bcrypt.hash(student.password, saltRounds);

                // Update the student's password
                student.password = hashedPassword;
                await student.save();

                hashedCount++;
                console.log(`âœ… Student ${student.studentID}: Password hashed successfully`);

            } catch (error) {
                errorCount++;
                console.error(`âŒ Error hashing password for student ${student.studentID}:`, error.message);
            }
        }

        // Print summary
        console.log('\nðŸ“Š Password Hashing Summary:');
        console.log(`âœ… Successfully hashed: ${hashedCount} passwords`);
        console.log(`â­ï¸  Already hashed: ${alreadyHashedCount} passwords`);
        console.log(`âŒ Errors: ${errorCount}`);
        console.log(`ðŸ“Š Total students processed: ${students.length}`);

    } catch (error) {
        console.error('âŒ Error in hashing process:', error);
    } finally {
        // Close database connection
        await mongoose.connection.close();
        console.log('\nâœ… Database connection closed');
        process.exit(0);
    }
};

// Run the script
const run = async () => {
    console.log('ðŸš€ Password Hashing Utility Script');
    console.log('===================================\n');

    // Confirm before proceeding
    console.log('âš ï¸  WARNING: This script will hash all plain text passwords in the database.');
    console.log('âš ï¸  Make sure you have a backup of your database before proceeding.\n');

    await connectDB();
    await hashExistingPasswords();
};

// Execute
run().catch(error => {
    console.error('âŒ Script failed:', error);
    process.exit(1);
});

backend/utlis/jwtHelpers.js.content:
const jwt = require('jsonwebtoken');

// Get JWT secret from environment variable or use a default (should be in .env in production)
const JWT_SECRET = process.env.JWT_SECRET || 'your-super-secret-jwt-key-change-this-in-production';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '24h'; // 24 hours by default

/**
 * Generate a JWT token for a student
 * @param {Object} payload - The data to encode in the token (e.g., studentId, studentID)
 * @returns {String} - The generated JWT token
 */
const generateToken = (payload) => {
    return jwt.sign(payload, JWT_SECRET, {
        expiresIn: JWT_EXPIRES_IN
    });
};

/**
 * Verify and decode a JWT token
 * @param {String} token - The JWT token to verify
 * @returns {Object} - The decoded token payload
 * @throws {Error} - If token is invalid or expired
 */
const verifyToken = (token) => {
    return jwt.verify(token, JWT_SECRET);
};

/**
 * Extract token from Authorization header
 * @param {String} authHeader - The Authorization header value
 * @returns {String|null} - The extracted token or null
 */
const extractTokenFromHeader = (authHeader) => {
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return null;
    }
    return authHeader.substring(7); // Remove 'Bearer ' prefix
};

module.exports = {
    generateToken,
    verifyToken,
    extractTokenFromHeader,
    JWT_EXPIRES_IN
};

backend/Controller/Admin/AdminAuthContro.content:
const Admin = require('../../Models/Admin');
const bcrypt = require('bcrypt');

// ðŸ”¥ Login Admin
exports.login = async (req, res) => {
    const adminID = req.body.adminID || req.body.AdminID;
    const password = req.body.password || req.body.Password;

    // ðŸ›‘ Validation
    if (!adminID || !password) {
        return res.status(400).json({
            message: "Admin ID and password are required",
            received: { adminID: !!adminID, password: !!password }
        });
    }

    try {
        const admin = await Admin.findOne({ adminID });
        if (!admin) {
            return res.status(404).json({ message: "Admin not found" });
        }

        const isMatch = await bcrypt.compare(password, admin.password);
        if (!isMatch) {
            return res.status(400).json({ message: "Invalid credentials" });
        }

        const sessionExpiry = new Date(Date.now() + 24 * 60 * 60 * 1000);
        await Admin.findByIdAndUpdate(admin._id, { sessionExpiry, lastLoginAt: new Date() });

        res.cookie('admin_token', admin._id, {
            httpOnly: true,
            maxAge: 24 * 60 * 60 * 1000,
            secure: process.env.NODE_ENV === 'production',
            sameSite: 'strict'
        });

        res.status(200).json({
            message: `Welcome ${admin.firstName} ${admin.lastName || ''}`.trim(),
            adminID: admin.adminID,
            email: admin.email
        });
    } catch (err) {
        console.error("Admin Login Error:", err);
        res.status(500).json({ message: "Server error during login" });
    }
};

// ðŸ”¥ Logout Admin
exports.logout = async (req, res) => {
    try {
        if (req.adminID) {
            await Admin.findByIdAndUpdate(req.adminID, { sessionExpiry: null });
        }
        res.clearCookie('admin_token');
        res.status(200).json({ message: "Admin logged out successfully" });
    } catch (err) {
        console.error("Admin Logout Error:", err);
        res.status(500).json({ message: "Server error during logout" });
    }
};

