const Student = require('../../Models/Student');
const bcrypt = require('bcrypt');

// ðŸ“ ADD NEW STUDENT
const addStudent = async (req, res) => {
    try {
        const {
            studentID,
            firstName,
            lastName,
            fathersName,
            mothersName,
            Address,
            grade,
            email,
            password,
            section
        } = req.body;

        console.log("ðŸ“¥ Add Student Request Body:", req.body);

        // ðŸ”¥ Validate required fields
        if (!studentID || !firstName || !fathersName || !mothersName || !Address || !grade || !email || !password || !section) {
            return res.status(400).json({ message: "All required fields must be filled" });
        }

        // ðŸŒŸ Check for duplicate studentID or email
        const existingStudent = await Student.findOne({
            $or: [{ studentID }, { email }]
        });

        if (existingStudent) {
            if (existingStudent.studentID === studentID) {
                return res.status(400).json({ message: "Student ID already exists" });
            }
            if (existingStudent.email === email) {
                return res.status(400).json({ message: "Email already exists" });
            }
        }

        // ðŸ”’ Hash password
        const hashedPassword = await bcrypt.hash(password, 12);

        // ðŸ“¦ Create new student
        const newStudent = new Student({
            studentID,
            firstName,
            lastName,
            fathersName,
            mothersName,
            Address,
            grade,
            section,
            email,
            password: hashedPassword
        });

        await newStudent.save();

        console.log("âœ… Student created successfully:", newStudent.studentID);

        res.status(201).json({
            message: "Student added successfully",
            student: {
                studentID: newStudent.studentID,
                firstName: newStudent.firstName,
                email: newStudent.email,
                grade: newStudent.grade
            }
        });

    } catch (err) {
        console.error("âŒ Error adding student:", err);

        if (err.code === 11000) {
            if (err.keyPattern.studentID) {
                return res.status(400).json({ message: "Student ID already exists" });
            }
            if (err.keyPattern.email) {
                return res.status(400).json({ message: "Email already exists" });
            }
        }

        res.status(500).json({ message: "Server error while adding student" });
    }
};

// ðŸ—‘ï¸ DELETE STUDENT
const deleteStudent = async (req, res) => {
    const { id } = req.params;

    try {
        const deletedStudent = await Student.findByIdAndDelete(id);
        if (!deletedStudent) {
            return res.status(404).json({ message: "Student not found" });
        }
        res.status(200).json({ message: "Student deleted successfully" });
    } catch (err) {
        console.error("âŒ Error deleting student:", err);
        res.status(500).json({ message: "Server error while deleting student" });
    }
};

// ðŸ“¦ GET ALL STUDENTS
const getAllStudents = async (req, res) => {
    try {
        const students = await Student.find().sort({ createdAt: -1 });
        res.status(200).json(students);
    } catch (err) {
        console.error("âŒ Error fetching students:", err);
        res.status(500).json({ message: "Server error while fetching students" });
    }
};

// ðŸ” GET STUDENT BY ID
const getStudentById = async (req, res) => {
    const { id } = req.params;

    try {
        const student = await Student.findById(id);
        if (!student) {
            return res.status(404).json({ message: "Student not found" });
        }
        res.status(200).json(student);
    } catch (err) {
        console.error("âŒ Error fetching student:", err);
        res.status(500).json({ message: "Server error while fetching student" });
    }
};

module.exports = {
    addStudent,
    deleteStudent,
    getAllStudents,
    getStudentById
};

